require(['jquery', 'leaflet', 'leafletSearch'], function($, leaflet, ls){
  'use strict';

  //Add a new marker and remove the old one
  function addMarker(oldMarker, map, e){
    if(oldMarker){
      oldMarker.remove();
    }
    return L.marker(e.latlng).addTo(map);
  }

  //Works only if the connection is secure (https etc.)
  function locateUser(map){
    var locMarker = L.circleMarker();
    map.locate({setView: true, maxZoom: 16, watch: false});
    map.on('locationfound', function(e){
      locMarker.remove();
      locMarker = L.circleMarker(e.latlng, {
        color: '#146ea7',
        fillColor: '#146ea7',
        fillOpacity: 0.4,
        radius: 10
      }).bindPopup('Current location').addTo(map);
    })
  }

  //Function for adding navigation control
  function addNavigation(map){
      L.Control.navigation = L.Control.extend({
        onAdd: function(map){
          var container = L.DomUtil.create('div', 'leaflet-control-loc');
          var locateIcon = L.DomUtil.create('div', 'fa fa-map-marker');
          locateIcon.setAttribute('title', 'Current location');
          locateIcon.setAttribute('aria-label', 'Current location');
          container.appendChild(locateIcon);
          L.DomEvent.on(container, 'click', L.DomEvent.stopPropagation)
          .on(container, 'click', L.DomEvent.stop)
          .on(container, 'click', this._locateUser, this);
          return container;
        },
        _locateUser: function(e){
          e.preventDefault();
          locateUser(this._map);
        }
      });

      (new L.Control.navigation({position: 'topright'})).addTo(map);
  }

  //Function for adding search control
  function addSearch(map){
    map.addControl(new L.Control.Search({
      url: 'http://nominatim.openstreetmap.org/search?format=json&q={s}',
      jsonpParam: 'json_callback',
      propertyName: 'display_name',
      propertyLoc: ['lat', 'lon'],
      marker: false,
      autoCollapse: true,
      autoType: false,
      minLength: 2,
      position: 'topright'
    }));
  }

  function init(){
    $$('.simplemap-preview').each(function(element){
      var options = {};
      options.mapId = element.id;
      var zoom = element.dataset.zoom;
      options.zoom = zoom !== undefined ? zoom : 14;
      options.tiles = element.dataset.tiles !== undefined ? element.dataset.tiles : 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png';
      var map = L.map(options.mapId).setView(element.dataset.defaultloc.split(','), options.zoom);
      L.tileLayer(options.tiles, {
        attribution: '&copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a>',
        maxZoom: 18,
        id: 'mapbox.streets'
      }).addTo(map);

      //Get hidden marker position field
      #set ($markerPosPropName = $xwiki.getClass('Map.Code.SimpleMap.SimpleMapClass').getProperties()[0].getName())
      var markerPosInputId = '${markerPosPropName}';
      //Add a default marker to the map
      var marker = L.marker($('#'+markerPosInputId).val().split(',')).addTo(map);
      //Handling the click on map to include marker and popup
      map.on('click', function(e){
        //Get value of popup
        #set ($popupDataPropName = $xwiki.getClass('Map.Code.SimpleMap.SimpleMapClass').getProperties()[1].getName())
        var popupInputId = '${popupDataPropName}';
        var popupInfo = $('#'+popupInputId).val();
        $('#'+markerPosInputId).val(e.latlng.lat+','+e.latlng.lng);
        marker = addMarker(marker, map, e);
        if(popupInfo){
          marker.bindPopup(popupInfo);
        }
      });
      //Adding the navigation control
      addNavigation(map);
      //Adding the search control
      addSearch(map);
    })
  }

  //Run the init() function when dom is loaded
  (XWiki && XWiki.domIsLoaded && init()) || document.observe('xwiki:dom:loaded', init);
});
