require(['jquery', 'leaflet', 'leafletSearch', 'leaflet-commons'], function($, leaflet, ls, leafletCommons){
  'use strict';

  function createSimpleMapPreview(element){
  }

  function init(){
    $$('.simplemap-preview').each(function(element){
      var options = leafletCommons.setCommonMapOptions(element);
      var data = {};
      data.location = element.dataset.defaultloc.split(',');
      var map = leafletCommons.createEmptyMap(options, data);

      //Get hidden marker position field
      #set ($markerPosPropName = $xwiki.getClass('Map.Code.SimpleMap.SimpleMapClass').getProperties()[0].getName())
      var markerPosInputId = '${markerPosPropName}';
      //Add a default marker to the map
      var marker = L.marker($('#'+markerPosInputId).val().split(',')).addTo(map);
      //Handling the click on map to include marker and popup
      map.on('click', function(e){
        //Get value of popup
        #set ($popupDataPropName = $xwiki.getClass('Map.Code.SimpleMap.SimpleMapClass').getProperties()[1].getName())
        var popupInputId = '${popupDataPropName}';
        var popupInfo = $('#'+popupInputId).val();
        $('#'+markerPosInputId).val(e.latlng.lat+','+e.latlng.lng);
        marker = leafletCommons.addMarker(marker, map, e);
        if(popupInfo){
          marker.bindPopup(popupInfo);
        }
      });
    });
  }

  //Run the init() function when dom is loaded
  (XWiki && XWiki.domIsLoaded && init()) || document.observe('xwiki:dom:loaded', init);
});
