require(['jquery', 'leaflet', 'leafletSearch', 'leaflet-commons'], function ($, leaflet, ls, leafletCommons) {
  'use strict';

  function init()
  {
    $$('.flmap-preview').each(function (element) {
      var options = leafletCommons.setCommonMapOptions(element);
      var data = {};
      data.location = element.dataset.defaultloc.split(',');
      var map = leafletCommons.createEmptyMap(options, data);

      //Extending marker to include information for deleting form fields
      var flMarker = L.Marker.extend({
        options: {
          markerNumber: 0
        }
      });
      var marker;
      var i = 1;
      map.on('click', function(e){
        marker = new flMarker(e.latlng, { markerNumber: i }).bindTooltip('Marker ' + i);
        marker.on('click', function(){
          $('.form-marker-'+this.options.markerNumber).remove();
          this.remove();
        });
        marker.addTo(map);
        var formsContainer = L.DomUtil.get('popup-forms');
        var markerInfoContainer = L.DomUtil.create('div', 'form-marker-'+i, formsContainer);
        $(markerInfoContainer).html('<h4>For Marker ' + i + '</h4>\
        <input type="hidden" name="markerLocation" value=' + e.latlng.lat + ',' + e.latlng.lng + ' />\
        <dt><label for="category">$services.localization.render("intmap.FLMapEditor.category")</label></dt>\
        <dd><input type="text" name="category"/></dd>\
        <dt><label for="popupInfo">$services.localization.render("intmap.popupData")</label></dt>\
        <dd><textarea name="popupInfo"></textarea></dd>');
        i++;
      });
    });
  }

  //Run the init() function when dom is loaded
  (XWiki && XWiki.domIsLoaded && init()) || document.observe('xwiki:dom:loaded', init);
});
