require(['jquery', 'leaflet', 'leafletSearch', 'leaflet-commons'], function($, leaflet, ls, leafletCommons){
  'use strict';

  function createSimpleMapPreview(element){
  }

  function init(){
    $$('.pathmap-preview').each(function(element){
      var options = leafletCommons.setCommonMapOptions(element);
      var data = {};
      data.location = element.dataset.defaultloc.split(',');
      var map = leafletCommons.createEmptyMap(options, data);

      //Get hidden input for source and destination
      var sourceInput = $('#sourceLocation');
      var destinationInput = $('#destinationLocation');
      var markersCount = 0;
      var sourceMarker, destinationMarker;
      map.on('click', function(e){
        if(!sourceMarker){
          sourceMarker = L.marker(e.latlng).bindTooltip('Source').addTo(map);
          sourceInput.val(e.latlng.lat + ',' + e.latlng.lng);
          sourceMarker.on('click', function(){
            this.remove();
            sourceMarker = undefined;
          });
          sourceMarker.on('mouseover', function(){
            this.openTooltip();
          });
          sourceMarker.on('mouseout', function(){
            this.closeTooltip();
          });
        }else if(!destinationMarker){
          destinationMarker = L.marker(e.latlng).bindTooltip('Destination').addTo(map);
          destinationInput.val(e.latlng.lat + ',' + e.latlng.lng);
          destinationMarker.on('click', function(){
            this.remove();
            destinationMarker = undefined;
          });
          destinationMarker.on('mouseover', function(){
            this.openTooltip();
          });
          destinationMarker.on('mouseout', function(){
            this.closeTooltip();
          });
        }
      });
      var routesFeatureGroup = L.featureGroup();
      $('#remove-pathpoints').on('click', function(){
        if(sourceMarker){
          sourceMarker.remove();
          sourceMarker = undefined;
        }
        if(destinationMarker){
          destinationMarker.remove();
          destinationMarker = undefined;
        }
        if(routesFeatureGroup){
          routesFeatureGroup.clearLayers();
        }
      });
      $('#generate-path').on('click', function(){
        if(sourceMarker && destinationMarker){
          if(routesFeatureGroup){
            routesFeatureGroup.clearLayers();
          }
          var data = {
            source: [sourceMarker.getLatLng().lat, sourceMarker.getLatLng().lng],
            destination: [destinationMarker.getLatLng().lat, destinationMarker.getLatLng().lng]
          };
          leafletCommons.generatePath(data, function(routes){
            routesFeatureGroup = routes;
            routesFeatureGroup.addTo(map);
            map.fitBounds(routesFeatureGroup.getBounds());
          });
        }
      });
    });
  }

  //Run the init() function when dom is loaded
  (XWiki && XWiki.domIsLoaded && init()) || document.observe('xwiki:dom:loaded', init);
});
