#set($columns = ["doc.title", "markerLocation", "doc.creationDate", "_actions"])
#set($columnsProperties = {
    "doc.title" : { "type" : "text", "link" : "view", "html" : "true", "sortable":true },
    "markerLocation" : { "type" : "text" },
    "_actions" : {"actions": ["edit","delete"]}
})
#set($options = {
   "className":"Map.Code.SimpleMap.SimpleMapClass",
   "translationPrefix" : "simpleMap.livetable.",
   "tagCloud" : true,
   "rowCount": 10
})
#livetable("simpleMapTable" $columns $columnsProperties $options)
